const Ws = {
    oneTarget: "migaku-one-target",
    oneTargetTagged: "migaku-one-t-tagged",
    onlyUnknownRuby: "migaku-only-unknown-ruby",
    fiveStar: "migaku-five-star",
    fourStar: "migaku-four-star",
    threeStar: "migaku-three-star",
    twoStar: "migaku-two-star",
    oneStar: "migaku-one-star",
    noStar: "migaku-no-star"
};

const os = {
    textToParse: "textToParse",
    preferences: "preferences",
    _$languageCode$_: "languageCode"
};

const runtimeId = chrome.runtime.id;

function sendRuntimeMessage(message, options, callback = (() => {})) {
    chrome.runtime.sendMessage(message, options, callback)
}

var textNodes = []
function replaceRubyElements(t) {
    const rubyElements = t.getElementsByTagName("ruby");
    for (let i = rubyElements.length - 1; i > -1; i--) {
        const rubyElement = rubyElements[i];
        rubyElement.outerHTML = rubyElement.innerHTML.replace(/\<rt.*?\>.+?\<\/rt.*?\>/g, "").replace(/\<.+?\>(.+?)\<.+?\>/g, "$1")
    }
}

function getTextNodeElements(element) {
    if (element.nodeType === 1) {
        let e = !1;
        if (element == document.body && (e = !0), !e && r(element) || element.classList && (element.classList.contains("migaku-word-cont") || element.classList.contains("migaku-ignore") || element.classList.contains("migaku-word") || element.classList.contains("migaku-learning-status") || element.classList.contains(Ws.oneTarget) || element.classList.contains("migaku-status-bar") || element.classList.contains("migaku-no-reading")) || element.nodeName && ("RT" == element.nodeName || "RP" == element.nodeName)) {
            return;
        }
        for (var n = 0; n < element.childNodes.length; n++) {
            getTextNodeElements(element.childNodes[n]);
        }
    } else if (element.nodeType === 3) {
        textNodes.push(element);
    }
}

function r(t) {
    return null === t.offsetParent
}

function pitchColorTextNode(textNode, preferences = {}, languageCode = "ja") {
    const params = {
        [os.textToParse]: textNode.data,
        [os.preferences]: {
            pitchShapes: true,
            wordStatus: false,
            ...preferences
        },
        [os._$languageCode$_]: languageCode ?? "ja"
    };

    return new Promise((resolve) => {
        sendRuntimeMessage(runtimeId, params, (async function(modifiedTextNode) {
            await applyTextNodeChanges(textNode, modifiedTextNode);
            resolve();
        }))
    })
}

async function applyTextNodeChanges(textNode, modifiedTextNode) {
    var span = document.createElement("SPAN");
    span.innerHTML = modifiedTextNode;
    const parent = textNode.parentNode;
    if (parent) {
        parent.insertBefore(span, textNode);
        parent.removeChild(textNode);
    }
}

async function pitchColorSelectedTextNodes(preferences, languageCode) {
    await Promise.allSettled(textNodes.map(async (textNode) => {
        if (textNode.data !== "") {
            await pitchColorTextNode(textNode, preferences, languageCode)
        }
    }))
    
}

const changeFuriganaVisibility = (visible) => {
    document.querySelectorAll('.migaku-upper-ruby').forEach((el) => {
        if (visible) {
            el.style.removeProperty('display');
        } else {
            el.style.setProperty('display', 'none', 'important');
        }
    });
};

async function performMigakuParse(element) {
    if (!element) {
        element = document.body;
    }

    replaceRubyElements(element);
    getTextNodeElements(element);

    return new Promise((resolve) => {
        chrome.storage.local.get([KANJI_VIEW_SETTINGS.PITCH_SHAPES, KANJI_VIEW_SETTINGS.ENABLE_FURIGANA], (storage) => {
            pitchColorSelectedTextNodes({
                pitchShapes: !!storage[KANJI_VIEW_SETTINGS.PITCH_SHAPES],
            }).then(() => {
                const visible = !!storage[KANJI_VIEW_SETTINGS.ENABLE_FURIGANA]
                changeFuriganaVisibility(visible);
                resolve();
            });
        })
    }) 
}